(975, 145, '_trabajo_repetidor_4_imagen', 'field_5b53a96c949c1'),
(976, 145, 'trabajo_repetidor_4_titulo', '5. Armado Estructura'),
(977, 145, '_trabajo_repetidor_4_titulo', 'field_5b53a98b949c2'),
(978, 145, 'trabajo_repetidor_4_bajada_texto', 'Somos Multi sistema y Multi marca. Realizamos el armado tanto de estructuras fijas como con seguidor, de marcas como STI-Norland, NClave, Braux y Array.'),
(979, 145, '_trabajo_repetidor_4_bajada_texto', 'field_5b53a99c949c3'),
(980, 145, 'trabajo_repetidor_4_', ''),
