(1320, 182, 'trabajo_repetidor_1_titulo', '2. Descarga y Repartición de Materiales'),
(1321, 182, '_trabajo_repetidor_1_titulo', 'field_5b53a98b949c2'),
(1322, 182, 'trabajo_repetidor_1_bajada_texto', 'Realizamos la descarga de los materiales de Estructura y los pallets de Paneles Fotovoltaicos. Y posteriormente hacemos los repartos de estos materiales directo al terreno a instalar.'),
(1323, 182, '_trabajo_repetidor_1_bajada_texto', 'field_5b53a99c949c3'),
(1324, 182, 'trabajo_repetidor_1_', ''),
(1325, 182, '_trabajo_repetidor_1_', 'field_5b53a9b1949c4'),
